let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));


let fieldId = widgetContext.stateController.getStateParams().entityId;

openAddSensorDialog();

function openAddSensorDialog() {
    customDialog.customDialog(htmlTemplate, AddSensorDialogController).subscribe();
}

function AddSensorDialogController(instance) {
    let vm = instance;

    vm.widgetContext = widgetContext;
    vm.step = 1;
    vm.sensor = null;

    vm.hasLocation = false;
    vm.locationSubscription = null;


    vm.addSensorFormGroup = vm.fb.group({
      name: ['', [vm.validators.required]],
      label: [null]
    });

    vm.cancel = function() {
        cancelLocationSubscription();
        deleteEntityObservable().subscribe(
            function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };

    vm.next = function() {
        vm.addSensorFormGroup.markAsPristine();
        saveEntityObservable().subscribe(
            function (entity) {
                const tasks = [saveAttributes(entity.id)];
                if (!vm.sensor) {
                    tasks.push(saveRelation(entity.id));
                }
                vm.sensor = entity;
                widgetContext.rxjs.forkJoin(tasks).subscribe(
                    function () {
                        createLocationSubscription().subscribe(
                            function() {
                                vm.step = 2;
                            }
                        );
                    }
                );
            }
        );
    };

    vm.back = function() {
        cancelLocationSubscription();
        vm.step = 1;
    }

    vm.submit = function() {
        cancelLocationSubscription();
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
    }


    function createLocationSubscription() {
        var subscriptionInfo = {
            type: 'entity',
            entityType: vm.sensor.id.entityType,
            entityId: vm.sensor.id.id,
            attributes: [{ name: 'latitude' }, {name: 'longitude'}]
        };
        var options = {
            callbacks: {
                onDataUpdated: function(subscription) {
                    checkHasLocation(subscription.data);
                }
            }
        };
        return widgetContext.subscriptionApi.createSubscriptionFromInfo(
                            'latest', [ subscriptionInfo ], options, false, true).pipe(
            widgetContext.rxjs.tap(function(locationSubscription) {
                vm.locationSubscription = locationSubscription;
            })
        );

    }

    function checkHasLocation(data) {
        var hasLocation = false;
        if (data.length === 2) {
            var latitude, longitude;
            for (var i = 0; i < data.length; i ++) {
                var datasourceData = data[i];
                if (datasourceData.dataKey.name === 'latitude') {
                    latitude = datasourceData.data[0][1];
                } else if (datasourceData.dataKey.name === 'longitude') {
                    longitude = datasourceData.data[0][1];
                }
            }
            if (isNumeric(latitude) && isNumeric(longitude)) {
                hasLocation = true;
            }
        }
        vm.hasLocation = hasLocation;
    }

    function isNumeric(val) {
        if (typeof val === "number") {
            return true;
        } else if (typeof val === "string") {
            return !isNaN(val) && 
                !isNaN(parseFloat(val));
        } else {
            return false;
        }
    }

    function cancelLocationSubscription() {
        if (vm.locationSubscription) {
            widgetContext.subscriptionApi.removeSubscription(vm.locationSubscription.id);
            vm.locationSubscription = null;
        }
    }

    function saveEntityObservable() {
        return getOrCreateSmartIrrigationDeviceGroup().pipe(
          widgetContext.rxjs.switchMap((smartIrrigation) => {
            const formValues = vm.addSensorFormGroup.value;
            let sensor = {
                name: formValues.name,
                type: 'SI Soil Moisture Sensor',
                label: formValues.label
            };
            if (vm.sensor) {
                sensor.id = vm.sensor.id;
            }
            return deviceService.saveDevice(sensor, smartIrrigation.id.id);
          })
        );
    }

    function deleteEntityObservable() {
        if (vm.sensor) {
            return deviceService.deleteDevice(vm.sensor.id.id);
        } else {
            return widgetContext.rxjs.of(null);
        }
    }

    function saveAttributes(entityId) {
        if (!vm.hasLocation) {
            return attributeService.getEntityAttributes(fieldId, 'SERVER_SCOPE', ['perimeter']).pipe(
                widgetContext.rxjs.mergeMap(function (attributes) {
                    var polygon;
                    if (attributes.length && attributes[0].key === 'perimeter') {
                       polygon = attributes[0].value;
                    }
                    var latLng = calculatePolygonCenter(polygon);
                    let attributesArray = [
                        {key: 'latitude', value: latLng[0]},
                        {key: 'longitude', value: latLng[1]}
                    ];
                    return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
                })
            );
        } else {
            return widgetContext.rxjs.of(null);
        }
    }

    function saveRelation(entityId) {
        let relation = {
            type: 'FieldToMoistureSensor',
            typeGroup: 'COMMON',
            to: entityId,
            from: fieldId
        };
        return entityRelationService.saveRelation(relation);
    }

    function getOrCreateSmartIrrigationDeviceGroup() {
      return getEntityGroupByName("Smart Irrigation", "DEVICE").pipe(
          widgetContext.rxjs.switchMap((group) => {
              if (group) {
                  return widgetContext.rxjs.of(group);
              } else {
                  var smartIrrigation = {
                    type: 'DEVICE',
                    name: 'Smart Irrigation'
                  };
                  return entityGroupService.saveEntityGroup(smartIrrigation);
              }
          })
      );
    }

    function getEntityGroupByName(groupName, groupType) {
      var entityGroupsPageLink = widgetContext.pageLink(10, 0, groupName);
      return entityGroupService.getEntityGroupsByPageLink(entityGroupsPageLink, groupType, {ignoreLoading: true}).pipe(
          widgetContext.rxjs.map((data) => {
            if (data.data.length) {
                return data.data.find((group) => group.name === groupName);
            } else {
                return null;
            }
          })
      );
    }

    function calculatePolygonCenter(polygon) {
        if (polygon && Array.isArray(polygon)) {
            var length = polygon.length;
            var latSum = 0;
            var lngSum = 0;
            for (var i = 0; i < length; i ++) {
                var polyLatLng = polygon[i];
                latSum += polyLatLng[0];
                lngSum += polyLatLng[1];
            }
            return [latSum / length, lngSum / length];
        } else {
            return [0,0];
        }
    }

}

