var lastActive = data[0]['lastActive'] || 'N/A';
var currentZone = data[0]['currentZone'] || 'Unknown';
var battery = data[0]['battery'] || 0;
var activeStatus = data[0]['active'] || 'inactive';
var statusClass = activeStatus.toLowerCase() === 'active' ? 'active' : 'inactive';

var initials = name.split(' ').map(n => n[0]).join('').toUpperCase();

var moistureTs = data[0]['moisture|ts'];
var lastSeen = 'N/A';

if (moistureTs) {
    var elapsed = new Date().getTime() - moistureTs;
    var MIN = 60 * 1000;
    var HOUR = 60 * MIN;
    var DAY = 24 * HOUR;

    if (elapsed >= DAY) {
        lastSeen = "~" + Math.floor(elapsed / DAY) + " day";
    } else if (elapsed >= HOUR) {
        lastSeen = "~" + Math.floor(elapsed / HOUR) + " hr";
    } else if (elapsed >= MIN) {
        lastSeen = Math.floor(elapsed / MIN) + " min ago";
    } else {
        lastSeen = "Just now";
    }
}

