if (type === 'SI Soil Moisture Sensor') {
    var avatarSize = 48;
    var statusSize = 18;
    var statusRight = 1;
    var statusTop = 3;
    var avatarPadding = 3;
    
    var moisture = data['moisture'];
    var minMoistureThreshold = data['minMoistureThreshold'];
    var maxMoistureThreshold = data['maxMoistureThreshold'];
    
    // Determine if sensor is active (has recent data)
    var isActive = typeof moisture === 'number' && moisture !== null && moisture !== undefined;
    
    // Set border properties based on active status
    var borderSize = 1;
    var borderColor = '#3AA94D'; // Green for active
    var borderOpacity = 0.8;
    if (!isActive) {
        borderSize = 3;
        borderColor = '#999999'; // Gray for inactive
        borderOpacity = 1;
    }
    
    var avatarOffsetX = 0;
    var avatarOffsetY = statusTop;
    var svgWidth = avatarSize + avatarPadding * 2 + statusRight;
    var svgHeight = avatarSize + avatarPadding * 2 + statusTop;
    
    var svgStr = '<svg width="' + svgWidth + '" height="' + svgHeight + '" viewBox="0 0 ' + svgWidth + ' ' + svgHeight + '" fill="none" xmlns="http://www.w3.org/2000/svg">';
    
    var photo = data.photo || 'data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 48 48"><rect width="48" height="48" fill="%23f0f0f0"/><text x="24" y="28" text-anchor="middle" fill="%23999" font-family="Arial" font-size="12">Sensor</text></svg>';
    
    svgStr += getSensorPhotoCircleSvg(avatarSize, avatarPadding, borderSize, borderColor, borderOpacity, photo, avatarOffsetX, avatarOffsetY);
    svgStr += getSensorStatusSvg(statusSize, statusRight, avatarSize, avatarPadding, isActive);
    
    svgStr += '</svg>';

    var encodedSvg = encodeURIComponent(svgStr);
    var svgUrl = 'data:image/svg+xml,' + encodedSvg;
    
    return {
        url: svgUrl,
        size: svgHeight,
        markerOffset: [avatarPadding + avatarSize / 2, statusTop + avatarPadding + avatarSize / 2],
        tooltipOffset: [0, - (statusTop + avatarPadding + avatarSize / 2)]
    };
} else {
    return null;
}

function getSensorPhotoCircleSvg(avatarSize, avatarPadding, borderSize, borderColor, borderOpacity, photo, offsetX, offsetY) {
    var cx = avatarSize / 2;
    var cy = avatarSize / 2;
    var r = avatarSize / 2;
    return '<defs>' +
        '<clipPath id="sensorCircleView">' +
            '<circle cx="'+cx+'" cy="'+cy+'" r="'+r+'" fill="#FFFFFF" />' +
        '</clipPath>' +
    '</defs>' +
    '<g transform="translate('+(offsetX + avatarPadding)+','+(offsetY + avatarPadding)+')">' +
        '<circle cx="'+cx+'" cy="'+cy+'" r="'+(r+borderSize)+'" fill="'+borderColor+'" opacity="'+borderOpacity+'"/>' +
        '<image ' +
          'width="'+avatarSize+'" ' +
          'height="'+avatarSize+'" ' +
          'href="'+photo+'" ' +
          'clip-path="url(#sensorCircleView)"' +
        '/>' +
    '</g>';
}

function getSensorStatusSvg(statusSize, statusRight, avatarSize, avatarPadding, isActive) {
    var offsetX = avatarSize + avatarPadding*2 - statusSize + statusRight;
    var offsetY = 0;
    var statusShape;
    
    if (isActive) {
        // Green circle with pulsing animation for active sensor
        statusShape = '<circle cx="9" cy="9" r="9" fill="#3AA94D">' +
                      '<animate attributeName="opacity" values="1;0.6;1" dur="2s" repeatCount="indefinite"/>' +
                      '</circle>';
    } else {
        // Gray circle with X mark for inactive sensor
        statusShape = '<rect width="18" height="18" rx="9" fill="#999999"/>' +
                      '<path d="M12.7071 5.29289C13.0976 5.68342 13.0976 6.31658 12.7071 6.70711L10.4142 9L12.7071 11.2929C13.0976 11.6834 13.0976 12.3166 12.7071 12.7071C12.3166 13.0976 11.6834 13.0976 11.2929 12.7071L9 10.4142L6.70711 12.7071C6.31658 13.0976 5.68342 13.0976 5.29289 12.7071C4.90237 12.3166 4.90237 11.6834 5.29289 11.2929L7.58579 9L5.29289 6.70711C4.90237 6.31658 4.90237 5.68342 5.29289 5.29289C5.68342 4.90237 6.31658 4.90237 6.70711 5.29289L9 7.58579L11.2929 5.29289C11.6834 4.90237 12.3166 4.90237 12.7071 5.29289Z" fill="white"/>';
    }
    
    return '<g transform="translate('+offsetX+','+offsetY+')">' +
           statusShape + 
           '</g>';
}

var type = data['type'];
if (type === 'SI Soil Moisture Sensor') {
    var text = getSensorText();
    var defaultMarkerWidth = 20;
    var width = defaultMarkerWidth + getTextWidth(text);
    var svgStr = getSensorSvg(width, text);
    var encodedSvg = encodeURIComponent(svgStr);
    var svgUrl = 'data:image/svg+xml,' + encodedSvg;
    return {
        url: svgUrl,
        size: width,
        markerOffset: [defaultMarkerWidth / 2, defaultMarkerWidth / 2],
        tooltipOffset: [width * 0.5 - defaultMarkerWidth / 2, - (defaultMarkerWidth / 2)]
    };
} else {
    return null;
}

function getSensorSvg(width, text) {
    var color = '#FFFFFF';
    var background = getSensorBackground(color, width);
    var svgWidth = width;
    var svgHeight = 20;
    var opacity = 1;
    var sensorSvg = '<svg opacity="' + opacity + '" width="' + svgWidth + '" height="' + svgHeight + '" viewBox="0 0 ' + svgWidth + ' ' + svgHeight + '" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                    background;
    if (text !== '') {
        var transform = '8 0';
        sensorSvg += '<text transform="translate(' + transform + ')" fill="black" xml:space="preserve" style="white-space: pre" font-family="Roboto" font-size="12" font-weight="600" letter-spacing="0em"><tspan x="0.10664" y="14.2637">' + text + '</tspan></text>';
    }
    sensorSvg += '</svg>';
    return sensorSvg;    
}

function getSensorBackground(color, width) {
     if (width === 20) {
         return '<circle cx="10" cy="10" r="10" fill="'+ color +'"/>';
     } else {
         return '<circle cx="10" cy="10" r="10" fill="'+ color +'"/>' + 
                '<circle cx="' + (width - 35) + '" cy="10" r="10" fill="'+ color +'"/>' +
                '<rect x="10" y="0" width="' + (width - 45) + '" height="20" rx="1" fill="'+ color +'"/>';
     }
}

function getSensorText() {
    if (data['moisture']) {
        var moisture = Number(data['moisture']);
        return moisture + '%';
    } else {
        return '';
    }
}

function getTextWidth(text) {
    var width = 0;
    for (var i = 0; i < text.length; i++) {
        var charWidth;
        var character = text.charAt(i);
        if (character === ' ') {
            charWidth = 8;
        } else if (character === '-') {
            charWidth = 7;
        } else if (character === '') {
            charWidth = 5;
        } else if (!isNaN(character * 1)){
            charWidth = 9;
        } else {
            // Upper case
            if (character === character.toUpperCase()) {
                charWidth = 12;
            // Lower case    
            } else {
                charWidth = 10;
            }
        }
        width += charWidth;
    }
    return width;
}

